import React, { useState } from "react";
import Navbar from './components/layout/Navbar'; 
import Footer from './components/layout/Footer';
import LoginForm from "./components/auth/LoginForm";
import RegisterForm from "./components/auth/RegisterForm";
import ForgotPasswordForm from "./components/auth/ForgotPasswordForm";
import ProductFilter from './components/ProductFilter';
import ContentList from './components/ContentList';
import CartPreview from './components/CartPreview';
import "./styles/authStyles.css"; // Asegúrate de que los estilos estén correctamente importados

const App = () => {
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);

  const products = [];  // Replace with actual data
  const cartItems = [];  // Replace with actual data

  return (
    <div>
      {/* Barra de navegación */}
      <Navbar />

      {/* Botón de Login en la esquina superior derecha */}
      <button
        className="login-button"
        onClick={() => setShowAuthModal(true)}
      >
        Iniciar Sesión
      </button>

      {/* Modal para Login, Registro y Recuperación de Contraseña */}
      {showAuthModal && (
        <div className="auth-modal">
          <div className="auth-modal-content">
            <span
              className="close"
              onClick={() => {
                setShowAuthModal(false);
                setShowForgotPassword(false);
              }}
            >
              &times;
            </span>

            {!showForgotPassword ? (
              <>
                <LoginForm onForgotPassword={() => setShowForgotPassword(true)} />
                <RegisterForm />
              </>
            ) : (
              <ForgotPasswordForm onClose={() => setShowForgotPassword(false)} />
            )}
          </div>
        </div>
      )}

      {/* Funcionalidades principales */}
      <ProductFilter />
      <ContentList products={products} />
      <CartPreview cartItems={cartItems} />

      {/* Pie de página */}
      <Footer />
    </div>
  );
};

export default App;
